/**
 * Type declarations for Vite environment variables injected by Electron Forge.
 * These constants are provided at build time by the Electron Forge Vite plugin.
 */

// URL to the Vite dev server when in development mode
declare const MAIN_WINDOW_VITE_DEV_SERVER_URL: string | undefined;

// Name of the entry point/HTML file for the renderer process
declare const MAIN_WINDOW_VITE_NAME: string;
